package arrays;import java.util.Scanner;public class MinNubersArrays {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        System.out.print("Введите массив: ");        int lengthMas = scanner.nextInt();        int[] numbers = new int[lengthMas];        System.out.print("Заполните массив целыми числами: ");        for (int i = 0; i < lengthMas; i++) {            numbers[i] = scanner.nextInt();        }        int minNumbers = numbers[0];        for (int i = 0; i < lengthMas; i++) {            if (numbers[i] < minNumbers) {                minNumbers = numbers[i];            }        }        System.out.println("Минимальное число в массиве: " + minNumbers);    }}