package arrays;import java.util.Scanner;public class SumArrays {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        System.out.println("Введите массив целых чисел");        int length = scanner.nextInt();        int[] numbers = new int[length];        System.out.println("Заполните массив целыми числами");        int sum = 0;        for (int i = 0; i < length; i++) {            numbers[i] = scanner.nextInt();            sum += numbers[i];        }        System.out.println(sum);    }}